import React from 'react'
import styled from 'styled-components'
import arrow from '../assets/arrow.png'



const Wrapper = styled.div`
  width:60vw;
  height:50vh;
  position: fixed;
  background-color: #fff;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-flow: column wrap;
  align-items: center;  
  padding: 24px;
`
const Input = styled.div`
    width: 100%;
    border: 1px solid rgb(221, 221, 221);
    border-radius: 6px;
    box-shadow: rgb(0 17 51 / 5%) 0px 3px 15px;
    padding: 12px;
    color: rgb(20, 30, 55);
    background: none;
    display: flex;
    align-items: center;
    overflow: hidden;
    justify-content: space-between;
`
const Button = styled.button`
  display: flex;  
  align-items: center;  
  justify-content: space-between;
  visibility: visible;
  width: 100%;
  height: 100%;
  opacity: 1;
  background: none !important;
  width: 100%;
  border: 1px solid rgb(221, 221, 221);
  border-radius: 6px;
  box-shadow: rgb(0 17 51 / 5%) 0px 3px 15px;
  padding: 12px;
  color: rgb(20, 30, 55);
  background: none;
`

function Currency() {
  return (
    <Wrapper>
      <form action="">
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <label htmlFor="amount" style={{
            fontWeight: 600,
            color: 'rgb(20, 30, 55)'
          }}>Amount</label>

          <Input>
            <span>1</span>
            <span>
              $
              <input type="text" />
            </span>
          </Input>

          <label htmlFor="amount" style={{
            fontWeight: 600,
            color: 'rgb(20, 30, 55)'
          }}>From
          </label>
          <div style={{ position: 'relative', display: 'inline-block' }}>
            <Button>
              <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                <div>
                  <img src="" alt="" />
                </div>
                <div>
                  <span>USD-</span>Us Dollar
                </div>
              </div>
            </Button>
          </div>

          <div>
            <button>
              svg
            </button>
          </div>

          <label htmlFor="amount" style={{
            fontWeight: 600,
            color: 'rgb(20, 30, 55)'
          }}>To
          </label>
          <div>
            <button>
              <div>
                <div>
                  <img src="" alt="" />
                </div>
                <div>
                  <span>USD-</span>Us Dollar
                </div>
              </div>
            </button>
          </div>

        </div>
      </form>

    </Wrapper>
  )
}
export default Currency

