import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import InputContainer from './InputContainer'
import CurrencyConverter from 'react-currency-conv';


const Wrapper = styled.div`
  width:60vw;
  height:50vh;
  position: fixed;
  background-color: #fff;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-flow: column wrap;
  align-items: center;  
  padding: 24px;
`

const Button = styled.button`
  display: flex;  
  align-items: center;  
  justify-content: space-between;
  visibility: visible;
  width: 100%;
  height: 100%;
  opacity: 1;
  background: none !important;
  width: 100%;
  border: 1px solid rgb(221, 221, 221);
  border-radius: 6px;
  box-shadow: rgb(0 17 51 / 5%) 0px 3px 15px;
  padding: 12px;
  color: rgb(20, 30, 55);
  background: none;
`
const Label = styled.label`
  font-weight: 600;
  color: rgb(20, 30, 55)

`

const BASE_URL = 'https://free.currconv.com'


const Currency = ({
  currencyOptions,
  selectedCurrency,
  onChangeCurrency,
  curr,
  onChangeToCurrency }) => {

  useEffect(() => {
    fetch(BASE_URL)
      .then(res => res.json())
      .then(data => console.log(data))
  }, [])

  const defaultValue = '1.00'
  return (


    <Wrapper>
      <form action="">
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <Label htmlFor="amount">Amount</Label>
          <InputContainer defaultValue={defaultValue} />

          <Label htmlFor="from">From</Label>
          <select
            id="from"
            value={selectedCurrency}
            onChange={onChangeCurrency}
          >
            {currencyOptions.map((option) => (
              <option key={option} value={option}>
                {option}
              </option>
            ))}
          </select>

          <Label htmlFor="amount">To</Label>
          <select value={curr} onChange={onChangeToCurrency}>
            {currencyOptions.map((option) => (
              <option value={option} key={option}>
                {option}
              </option>
            ))}
          </select>


        </div>
      </form>

    </Wrapper>
  )
}
export default Currency

