import React, { useState } from 'react'
import styled from 'styled-components'
import InputContainer from './InputContainer'




const Wrapper = styled.div`
  width:60vw;
  height:50vh;
  position: fixed;
  background-color: #fff;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-flow: column wrap;
  align-items: center;  
  padding: 24px;
`

const Button = styled.button`
  display: flex;  
  align-items: center;  
  justify-content: space-between;
  visibility: visible;
  width: 100%;
  height: 100%;
  opacity: 1;
  background: none !important;
  width: 100%;
  border: 1px solid rgb(221, 221, 221);
  border-radius: 6px;
  box-shadow: rgb(0 17 51 / 5%) 0px 3px 15px;
  padding: 12px;
  color: rgb(20, 30, 55);
  background: none;
`
const Label = styled.label`
  font-weight: 600;
  color: rgb(20, 30, 55)

`


const Currency = () => {


  return (
    <Wrapper>
      <form action="">
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          <Label htmlFor="amount">Amount</Label>
          <InputContainer />
          <Label htmlFor="from">From</Label>
          <div style={{ position: 'relative', display: 'inline-block' }}>
            <Button>
              <div style={{ display: 'flex', alignItems: 'center', width: '100%' }}>
                <ul style={{ width: 24, height: 'auto', flexShrink: 0, marginRight: 8 }}>
                  <img src="" alt="" style={{ objectFit: 'contain', width: '100%', height: '100%' }} />
                </ul>
                <div>
                  <span>USD-</span>Us Dollar
                </div>
              </div>
            </Button>
          </div>

          <div>
            <button>
              svg
            </button>
          </div>

          <Label htmlFor="amount">To</Label>
          <div>
            <button>
              <div>
                <div>
                  <img src="" alt="" />
                </div>
                <div>
                  <span>USD-</span>Us Dollar
                </div>
              </div>
            </button>
          </div>

        </div>
      </form>

    </Wrapper>
  )
}
export default Currency

