import Currency from "./components/Quotes"
import React, { useState, useEffect } from "react"

const QUOTES_API = 'https://api.quotable.io/random'

function App() {
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');
  const buttonClick = () => {
    setQuote('')
    setQuote(quote)
    setAuthor('')
    setAuthor(author)
  }

  useEffect(() => {
    fetch(QUOTES_API)
      .then(res => res.json())
      .then((data) => {
        console.log(data)
        setQuote(data.content)
        setAuthor(data.author)
      });
  }, [quote, author])

  return (
    <Currency quote={quote} author={author} buttonClick={buttonClick} />
  )

}

export default App
